import React from "react";

class Wallet extends React.Component {

  constructor(props) {
    super(props);
    this.updateMoney = this.props.updateMoney
    this.resourcesMined = this.props.resourcesMined
    this.interval=undefined;
    this.state = {
      totalResources: 10,
      currentResources: 10,
      canHarvest: true,
      shouldReset: false,
    };
  }

  handleResourceDepleted() {
    this.setState({
      currentResources: 0,
      canHarvest: false,
      totalResources: this.state.totalResources * 1.5,
    });
    setTimeout(() => {
      this.setState({ shouldReset: true });
    }, 500);
  }

  harvestResource = (harvestAmount) => {
    if (!this.state.canHarvest) return;
    // If no harvest amount is passed in, that means it is an auto dps harvest 
    // so the harvest amount is set to the dps
    harvestAmount ??= this.props.dps
    this.updateMoney(harvestAmount);
    this.setState({
       currentResources: this.state.currentResources - harvestAmount,
    }, () => {
      if (this.state.currentResources < 1) {
        this.handleResourceDepleted();
      }
    })
  };

  componentDidUpdate() {
    if (this.state.shouldReset) {
      this.setState({
        currentResources: this.state.totalResources,
        canHarvest: true,
        shouldReset: false,
      });
    }
  }

  componentDidMount() {
     this.interval = setInterval(() => {
      this.harvestResource();
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return (
      <>
        <div className="center-items">
          <img
            id="wallet"
            src={
              process.env.PUBLIC_URL +
              (this.state.currentResources > 0
                ? "/wallet/Wallet-0.png"
                : "/wallet/Wallet-1.png")
            }
            alt="broken img"
            style={{
              width: `${10 * (this.state.currentResources / this.state.totalResources) +
                10}rem`,
            }}
            onClick={() => {
              this.harvestResource(this.props.hph);
            }}
          ></img>
          <h1>
            {(
              (this.state.currentResources / this.state.totalResources) *
              100
            ).toFixed(0) + "%"}
          </h1>
        </div>
      </>
    );
  }
}

export default Wallet;
