    <h1>Helper Shop</h1>
    <div className="upgrade-buttons-container">
    <Helper
      title="Dog Miner"
      cost={5}
      dps={1*dpsMult}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />



    <Helper
      title="Super Dog"
      cost={100}
      dps={8*dpsMult}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Helper
      title="Mining Drill"
      cost={500}
