import Upgrade from "./Upgrade"
function UpgradeShop({multDps, getTotalMoney, updateTotalMoney, multMiningPower, shopData, updateShopData}) {

    return  ( 
      <>
    <h1>Upgrades</h1>
    <div className="upgrade-buttons-container">

    <Upgrade
      title="Mining"
      upgradeMult={2}
      description = " your mining strength" 
      cost={shopData[title].cost}
      upgradeClick={(cost,upgradeMult,title) => handleUpgradeClick(cost,upgradeMult,title)}
    />

    <Upgrade
      title="Helper"
      description = " your mining strength" 
      upgradeMult={2}
      cost={shopData[title].cost}
      upgradeClick={(cost,upgradeMult,title) => handleUpgradeClick(cost,upgradeMult,title)}
    />

    </div>
    </>
    )

    function handleUpgradeClick(cost,upgradeMult,title) {
      if(getTotalMoney()-cost<0) return false
      updateTotalMoney(cost*-1)
      // Upgrades either the helper or hit strength depending on the component title
      title === "Helper" ? multDps(upgradeMult) : multMiningPower(upgradeMult)
      return true
    }
}

export default UpgradeShop
