import React, {useState, useEffect} from "react";

function Wallet({ resourceClicked, resourcesMined }) {
  const [totalResources,setTotalResources] = useState((1+resourcesMined)*10);
  const [currentResources,setCurrentResources] = useState(totalResources);
  const [canHarvest,setCanHarvest] = useState(true)
  const [shouldReset,setShouldReset] = useState(false)

  useEffect(() => {
    if(shouldReset) {
    setCurrentResources(totalResources)
    setCanHarvest(true)
    setShouldReset(false)
    }
  }, [shouldReset,totalResources])

  return (
    <>
       <img
        id="wallet"
        src={process.env.PUBLIC_URL + currentResources>0 ? "/wallet/Wallet-0.png" : "/wallet/Wallet-1.png"}
        alt="broken img"
        onClick={() => {
          handleResourceClick()
        }}
      ></img>
      <h1>
        {(currentResources/totalResources*100).toFixed(1) + "%"}
      </h1>
    </>
  );

  function handleResourceClick() {
    if(!canHarvest) return

    resourceClicked(1,currentResources)
    setCurrentResources(currentResources-1)

    //console.log(currentResources)
    if(currentResources<=1){
    handleResourceMined()
    }
  }

   function handleResourceMined() {
    setCanHarvest(false)
      console.log("old" , totalResources)
      setTotalResources(totalResources*1.1)
      console.log("new", totalResources)
      setTimeout(() => {
       setShouldReset(true)
      },5000)
      console.log("")
  }


}







export default Wallet;
