import React from "react";

function Helper({title,upgradesClick,dps,description, cost}) {

    return (
      <div id="helper-container">

        <div>
          <div className="flex-space-between"> 
          <h2 className="block upgrade-title">{title}</h2>
          <h3 className="block right-aligned no-margin">{dps/10} m/s</h3>
          </div>
          <div className="upgrade-img-dsc-container">
            <img src={`/icons/${title}.png`} alt="placeholder" className="small-icons"/>
            <h3 className="no-margin">{description}</h3>
            <button
        className="btn block"
        onClick={() => {
          upgradesClick(title,cost,dps);
        }}
      >
        ${cost}
      </button>
          </div>
        </div>

      </div>
    );
}

export default Helper;
