import React from "react";

class WindMill extends React.Component {

  constructor(props) {
    super(props);
    this.updateWatts = this.props.updateWatts
    this.resourcesMined = this.props.resourcesMined
    this.interval=undefined;
    this.state = {
      totalResources: 10,
      currentResources: 10,
      canHarvest: true,
      shouldReset: false,
      velocity: 0,
    };
  }

  handleResourceDepleted() {
    this.setState({
      currentResources: 0,
      canHarvest: false,
      totalResources: this.state.totalResources * 1.5,
    });
    setTimeout(() => {
      this.setState({ shouldReset: true });
    }, 500);
  }

  harvestResource = (harvestAmount) => {
    if (!this.state.canHarvest) return;
    // If no harvest amount is passed in, that means it is an auto dps harvest 
    // so the harvest amount is set to the dps
    harvestAmount ??= this.props.dps
    this.updateWatts(harvestAmount);
    this.setState({
       currentResources: this.state.currentResources - harvestAmount,
        velocity: this.state.velocity + this.getAcceleration(harvestAmount/50) 
    }, () => {
      if (this.state.currentResources < 1) {
        this.handleResourceDepleted();
      }
    })
  };

  getAcceleration(force) {
    return force
  }

  getDeceleration(velocity) {
    console.log(velocity)
    if(velocity===0) {return 0}
    let deceleration = velocity*.1
    //return velocity-deceleration>=0 ? deceleration: velocity-deceleration
    return deceleration
  }

  acceleratedVelocity(speed) {
    return this.state.velocity + speed
  }

  componentDidUpdate() {
    if (this.state.shouldReset) {
      this.setState({
        currentResources: this.state.totalResources,
        canHarvest: true,
        shouldReset: false,
      });
    }
  }

   getVelocity(obj) {
    //console.log(this.state.velocity)
    return obj.state.velocity
  }

  rotateFan(fan) {  
    this.degree = 0;

      function rotate(degree, getVelocity, getDeceleration, obj) {
      fan.style.transform = 'rotate(' + degree + 'deg)'
       setTimeout(() => {
          degree+=getVelocity(obj);
          rotate(degree, getVelocity, getDeceleration, obj);
          obj.setState({
            velocity: obj.state.velocity - getDeceleration(getVelocity),
          })
          console.log(getDeceleration(getVelocity))
      },1); //lower this to increase speed 
  }
  rotate(this.degree, this.getVelocity, this.getDeceleration, this)
}

  componentDidMount() {
     this.interval = setInterval(() => {
      this.harvestResource();
    }, 1000);
    console.log(document.getElementsByClassName('wind-mill-fan')[0])
    this.rotateFan(document.getElementsByClassName('wind-mill-fan')[0],5)
  }
  componentWillUnmount() {
    clearInterval(this.interval);
    clearInterval(this.timer)
  }

  render() {
    return (
      <>
        <div className="center-items" id="wind-mill-container">
          <img
            className="wind-mill"
            src={
              process.env.PUBLIC_URL +
             "/windmill.png"
            }
            alt="broken img"
            style={{
              width: `${15}rem`,
            }}
            onClick={() => {
              this.harvestResource(this.props.miningPower);
            }}
          ></img>
             <img
            className="wind-mill-fan fan-rotate"
            src={
              process.env.PUBLIC_URL +
             "/windmill-fan.png"
            }
            alt="broken img"
            style={{
              width: `${15}rem`,
            }}
            onClick={() => {
              this.harvestResource(this.props.miningPower);
            }}
          ></img>
          <h1>
            {(
              (this.state.currentResources / this.state.totalResources) *
              100
            ).toFixed(0) + "%"}
          </h1>
        </div>
      </>
    );
  }
}

export default WindMill;
