import React, {useState, useEffect, useCallback} from "react";

function Wallet({ updateMoney, resourcesMined, harvestingPower, dps }) {
  const [totalResources,setTotalResources] = useState((1+resourcesMined)*10);
  const [currentResources,setCurrentResources] = useState(totalResources);
  const [canHarvest,setCanHarvest] = useState(true)
  const [shouldReset,setShouldReset] = useState(false)



  const handleResourceDepleted= useCallback(() => {
    setCurrentResources(0)
    setCanHarvest(false)
    setTotalResources(totalResources*1.1)
    setTimeout(() => {
     setShouldReset(true)
 },500)},[totalResources])


  const harvestResource = useCallback((harvestAmount) => {
      if(!canHarvest) return
    //console.log(currentResources)
    updateMoney(harvestAmount)
    setCurrentResources(c =>  c - harvestAmount)
    //console.log(currentResources-harvestAmount)
    if(currentResources<=1){
    handleResourceDepleted()
    }},
    [updateMoney,canHarvest,handleResourceDepleted],
  )



  useEffect(() => {
   // console.log("using effect")
    if(shouldReset) {
    setCurrentResources(totalResources)
    setCanHarvest(true)
    setShouldReset(false)
    }
  }, [shouldReset,totalResources])

  useEffect(() => {
    console.log("new use effect")
     const timer = setInterval(() => {
      console.log(currentResources)
      harvestResource(dps)
    }, 1000);
    return () => clearInterval(timer);
  },[dps])

  return (
    <>
    <div className="centerItems"> 
    <img
        id="wallet"
        src={process.env.PUBLIC_URL + currentResources>0 ? "/wallet/Wallet-0.png" : "/wallet/Wallet-1.png"}
        alt="broken img"
        style={{width: `${10*(currentResources/totalResources)+10}rem`}}
         //style={{width: `${20*.3}px`} , height: `${currentResources/totalResources*100}%`}}
        onClick={() => {
          harvestResource(harvestingPower)
        }}
      ></img>
      <h1>
        {(currentResources/totalResources*100).toFixed(0) + "%"}
      </h1>
    </div>

    </>
  );






  function harvestDps() {
    harvestResource(dps)
  }

}







export default Wallet;
