import HelperShop from "./HelperShop"
import React, {useState} from "react"
import UpgradeShop from "./UpgradeShop"

function Shop({incrementDps, getTotalWatts, updateTotalWatts, getDps, multMiningPower, shopData, updateShopData}) {
  const [dpsMult, setDpsMult] = useState(1)
  function multDps(multiplier) {
    setDpsMult(d => d * multiplier)
    incrementDps((multiplier-1)*getDps())
  }

  //console.log('RENDERING')
    return  ( 
    <div className="grid-div" id="right-div">

      <HelperShop
    incrementDps={incrementDps}
    getTotalWatts={getTotalWatts}
    updateTotalWatts={updateTotalWatts}
    dpsMult = {dpsMult}
    shopData= {shopData}
    updateShopData= {updateShopData}
        />
       <UpgradeShop
       multDps={multDps}
       multMiningPower = {multMiningPower}
       getTotalWatts={getTotalWatts}
       updateTotalWatts={updateTotalWatts}
       shopData={shopData}
       updateShopData={updateShopData}
       />

    </div>
    )

      // function getUpgradeState() {

      // }

      // function getHelperState() {

      // }
}

export default Shop
