import React from "react";
import Wallet from "./Wallet";
import Shop from "./shop/Shop"
import Authentication from "./Authentication";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      totalMoney: 0,
      dps: 0,
      // hph is the players harvest amount per hit
      hph: 1,
      shopData: [{title: "Dog Miner", cost: 5, numActive: 0}, {title : "Super Dog", cost: 50 , numActive: 0},
      { title: "Mining Drill", cost: 200 , numActive: 0},{ title: "Example", cost: 1000, numActive: 0},
      { title: "hph", cost: 200, numActive: 0}, {title: "dps", cost: 100, numActive: 0}],
    };
    console.log(this.state.shopData[0])
  }
  render() {
    return (
      <div id="body">
         <Authentication
          getGameInfo={this.getGameInfo()}
          setGameInfo={(info) => this.setGameInfo(info)}
        />
        <div className="grid-div" id="center-div">
          <h1>Money: {this.state.totalMoney}</h1>
          <h1 id="dps">DPS: {this.state.dps}</h1>
          <Wallet
            updateMoney={(moneyGained) => this.updateMoney(moneyGained)}
            hph={this.state.hph}
            dps={this.state.dps}
          />
        </div>
        <Shop
          incrementDps = {(increment) => this.incrementDps(increment)}
          getTotalMoney = {() => this.getTotalMoney()}
          updateTotalMoney = {(cost) => this.updateMoney(cost)}
          getDps = {() => this.state.dps}
          multHph= {(multFactor,) => this.multHph(multFactor)}
          updateHelpersData = {(title,property,newVal) => this.updateShopData(title,property,newVal)}
          // helpersData = {helpersData}
          // upgradesData = {upgradesData}
          // updateHelpersData = {(title,newVal,idx) => this.updateHelpersData(title,newVal,idx)}
        />

      </div>
    );
  }


  // updateHelpersData(title,newVal,idx) {


  //   this.setState(prevState => ({
  //     helpersData: {
  //       ...prevState.helpersData,
  //       [idx]: 
  //     }
  // }))
  // }

  updateShopData = (title, property, newVal) => {
    console.log(title);
    let updatedDataList = this.state.helpersData.map(item => 
      {
        if (item.title === title){
          return {...item, 
            [property]: newVal}; //gets everything that was already in item, and updates "done"
        }
        return item;
      });

    this.setState({helpersData: updatedDataList}); // set state to new object with updated list
  }

  incrementDps(increment) {
    this.setState({
      dps: this.state.dps + increment
    });
  }

  updateMoney(moneyToUpdate) {
    this.setState({
      totalMoney: this.state.totalMoney + moneyToUpdate
    })
  }

  multHph(multFactor) {
    this.setState({
      hph: this.state.hph * multFactor
    })
  }
  getTotalMoney() {
    return this.state.totalMoney
  }
  handleWalletClick(clickStrength,resourcesLeft) {
    var moneyGained = clickStrength
      // Set a state "fullyMined" to true that gets passed to the wallet so when fullyMined is true the wallet resets its variables
      // and then in this block, call a function like "resetResource" to reset it and get prepared to implement chest drops when the 
      // resource is fully mined.
    this.setState({
      totalMoney: this.state.totalMoney + moneyGained 
    });
  }




  getGameInfo() {
    return {
      totalMoney: this.state.totalMoney,
      dps: this.state.dps,
      hph: this.state.hph
    };
  }

  setGameInfo(info) {
    console.log(info)
    this.setState({
      totalMoney: info.totalMoney,
      dps: info.dps,
      hph: info.hph,
    });
  }
}

export default App;
