import React from "react";

class Helper extends React.Component {
  constructor(props) {
    super(props);
    this.title = props.title;
    this.upgradesClick = props.upgradesClick;
    this.cost = props.cost
    this.dps = props.dps*props.dpsMult
    this.helperCount = 2
    this.description = "a dog that mines for you, what more could you ask for"
  }
  render() {
    return (
      <div id="helper-container">
        <h3 className="right-aligned">#{this.helperCount}</h3>
        <div>
          <h2 className="block upgrade-title">{this.title}</h2>
          <h3 className="block no-margin">{this.dps} DPS</h3>
          <div className="upgrade-img-dsc-container">
            <img alt="placeholder"/>
            <h3 className="no-margin">{this.description}</h3>
          </div>
        </div>
      <button
        className="btn"
        onClick={() => {
          this.upgradeClicked();
        }}
      >
        ${this.cost}
      </button>
      </div>
    );
  }

  upgradeClicked() {
   let didPurchase = this.upgradesClick(this.cost,this.dps)
   if(didPurchase) this.cost = (this.cost*1.2).toFixed(0)
}
}

export default Helper;
