import React from 'react'

export default function HelperDisplayBar({shopData}) {

    function renderHelperDisplay(key,helpersActive) {
        return (

            <div className='small-margin center-items'>
                <img  alt={key}/>    
                 <h2 className='small-txt green'>{helpersActive}</h2>

            </div>
        )
    }

    function createHelperShop(shopData) {
        let helperDisplay = []
        for(let key in shopData) {
          if(shopData[key].dps)
          helperDisplay.push(renderHelperDisplay(key,shopData[key].numActive))
        }
        return {helperDisplay}
      }
  return (
    <div className='flex-jstfy-cent '>
    {createHelperShop(shopData)}
    </div>
  )
}
