import Helper from "./Helper"
function HelperShop({incrementDps, getTotalMoney, updateTotalMoney, dpsMult, shopData}) {

  function renderHelper({title,cost,dps}) {
    console.log(dpsMult)
    return <Helper
      key={title}
      title={title}
      dps={dps * dpsMult}
      cost={cost}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />
  }

    return  ( 
        <>
        <h1>Helper Shop</h1>
        <div className="upgrade-buttons-container">
        { shopData.map(data => {
          //If the data has a dps property it is for a helper component so it will be rendered
          // otherwise it is for an upgrade component and wont render
          return data?.dps ? renderHelper(data) : undefined
        }) }
        </div>
        </>
        )


