import Helper from "./Helper"
function HelperShop({incrementDps, getTotalWatts, updateTotalWatts, dpsMult,shopData, updateShopData}) {

  function renderHelper(title,{cost,dps,description}) {
    console.log(description)
    return <Helper
      key={title}
      title={title}
      dps={dps * dpsMult}
      cost={cost}
      description={description}
      upgradesClick={(title,cost,dps) => purchaseHelper(title, cost, dps)}
    />
  }

  function createHelperShop(shopData) {
    let helperContainer = []
    for(let key in shopData) {
      if(shopData[key].dps)
      helperContainer.push(renderHelper(key,shopData[key]))
    }
    return helperContainer
  }

    return  ( 
        <>
        <h1>Shop</h1>
        <div className="upgrade-buttons-container">
        { 
         createHelperShop(shopData)
        }
        </div>
        </>
        )    

    function purchaseHelper(title, cost, dps) {
      console.log(dps)
        if(getTotalWatts()<cost) return false
         updateShopData(title,{cost: (cost*1.5).toFixed(0), numActive: shopData[title].numActive+1, dps: dps})
         updateTotalWatts(cost*-1)
         incrementDps(dps)
         return true
     }
}

export default HelperShop
