import Upgrade from "./Upgrade"
function UpgradeShop({multDps, getTotalMoney, updateTotalMoney}) {

    return  ( 
      <>
    <h1>Upgrades</h1>
    <div className="upgrade-buttons-container">

    <Upgrade
      title="Upgrade Mining"
      upgradeMult={2}
      description = " your mining strength" 
      cost={3000}
      upgradeClick={(cost,upgradeMult) => handleUpgradeClick(cost,upgradeMult)}
    />

    <Upgrade
      title="Luck"
      task="upgradeLuck"
      description = " your mining strength" 
      upgradeMult={2}
      cost={5000}
      upgradeClick={(cost,upgradeMult) => handleUpgradeClick(cost,upgradeMult)}
    />

    </div>
    </>
    )

    function handleUpgradeClick(cost,upgradeMult) {
      if(getTotalMoney()-cost<0) return false
      updateTotalMoney(cost*-1)
      multDps(upgradeMult)
      return true
    }
}

export default UpgradeShop
