import React from "react";
import Axios from "axios";

class Authentication extends React.Component {

  constructor({ getGameInfo, setGameInfo, setLoggedIn }) {
    super()
    this.getGameInfo = getGameInfo
    this.setGameInfo = setGameInfo
    this.setLoggedIn = setLoggedIn
    this.state = {
      usernameReg: "",
      passwordReg: "",
      usernameLogin: "",
      passwordLogin: "",
      isLoggedIn: false,
    }
  }

   register = () => {
    Axios.post("http://localhost:3001/create-user", {
      username: this.state.usernameReg,
      password: this.state.passwordReg,
      gameInfo: this.getGameInfo(),
    }).then(() => {
      this.setLoggedIn()
    });

  };

   saveInfo = () => {
    if(!this.state.isLoggedIn) return 
    Axios.post("http://localhost:3001/save-data", {
      username: this.state.usernameLogin,
      password: this.state.passwordLogin,
      gameInfo: this.getGameInfo(),
    }).then(() => console.log("Auto Save", this.getGameInfo()));
  }

   login = () => {
    Axios.post("http://localhost:3001/login", {
      username: this.state.usernameLogin,
      password: this.state.passwordLogin,
    }).then((response) => {
      this.setState({
        isLoggedIn: true
      })
      this.setLoggedIn()
      console.log(response)
      this.setGameInfo(response.data)}
  )};

      componentDidMount() {
          this.interval = setInterval(this.saveInfo, 10000);
       }
       componentWillUnmount() {
         clearInterval(this.interval);
       }

  render() {
    return(
      <div>
    { !this.state.isLoggedIn && <div className="grid-div" id="left-div">
      <h1>Sign Up</h1>

      <input
        className="textbox"
        placeholder="Username.."
        onChange={(e) => {
          this.setState({
            usernameReg: e.target.value
          })
        }}
      ></input>

      <input
        className="textbox"
        placeholder="Password.."
        onChange={(e) => {
          this.setState({
            passwordReg: e.target.value
          })
        }}
      ></input>

      <button onClick={this.register}>Enter</button>
      <h1>Login</h1>

      <input
        className="textbox"
        placeholder="Username.."
        onChange={(e) => {
          this.setState({
            usernameLogin: e.target.value
          })
        }}
      ></input>

      <input
        className="textbox"
        placeholder="Password.."
        onChange={(e) => {
          this.setState({
            passwordLogin: e.target.value
          })
        }}
      ></input>
      <button onClick={this.login}>Enter</button>
    </div>}
    </div>
  )}
}

export default Authentication;
