import React from "react";
import Shop from "./shop/Shop"
import GameContainer from "./GameContainer";
import Authentication from "./Authentication";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      totalWatts: 0,
      dps: 0,
      // miningPower is the players harvest amount per hit
      miningPower: 1,
      shopData: {"Fan": {cost: 5, dps: 1, numActive: 0, description: "It's like a mini windmill"}, "Leaf Blower": {cost: 50 , dps: 5, numActive: 0, description: "Loud and powerful"},
       "Rocket": { cost: 20000 ,dps: 200, numActive: 0, description: "This might be overkill" },
      //, "Ex 4": {cost: 1000, dps: 100, numActive: 0},
      "blowingPower": {cost: 10, numActive: 0}, "wps":  {cost: 20, numActive: 0}},
      isLoggedIn: false,
      shopPanelActive: true
    };
  }
  render() {
    return (

      <div id="body">
        <img src="/icons/sunset-bkg-3.png" id="background-img" alt="img not loading"></img>

        <Authentication
          getGameInfo={this.getGameInfo.bind(this)}
          setGameInfo={(info) => this.setGameInfo(info)}
          setLoggedIn={() => {this.setLoggedIn()}}
        />

         <GameContainer
            totalWatts={this.state.totalWatts}
            updateWatts={(wattsGained) => this.updateWatts(wattsGained)}
            miningPower={this.state.miningPower}
            dps={this.state.dps}
            shopData={this.state.shopData}
          />

       {this.state.shopPanelActive &&  <Shop
          incrementDps = {(increment) => this.incrementDps(increment)}
          getTotalWatts = {() => this.getTotalWatts()}
          updateTotalWatts = {(cost) => this.updateWatts(cost)}
          getDps = {() => this.state.dps}
          multMiningPower= {(multFactor) => this.multMiningPower(multFactor)}
          updateShopData = {(title,newData) => this.updateShopData(title,newData)}
          shopData = {this.state.shopData}
          setShopPanelState = {(stateBool) => this.setShopPanelState(stateBool)}
        />}

      </div>
    );
  }

  updateShopData = (title, newData) => {
     let new_dict = this.state.shopData
      for(var key in new_dict) {
        if(key===title) new_dict[key] = newData
      }
      this.setState({shopData: new_dict}); // set state to new object with updated list
   }


  incrementDps(increment) {
    this.setState({
      dps: this.state.dps + increment
    });
  }

  updateWatts(wattsToUpdate) {
    //console.log(typeof wattsToUpdate)
    this.setState({
      totalWatts: +(this.state.totalWatts + wattsToUpdate).toFixed(2)
    })
  }

  multMiningPower(multFactor) {
    this.setState({
      miningPower: this.state.miningPower * multFactor
    })
  }
  getTotalWatts() {
    return this.state.totalWatts
  }

  setLoggedIn() {
    this.setState({
      isLoggedIn: true
    })
  }

  setShopPanelState(stateBool) {
    this.setState({
      shopPanelActive: stateBool
    })
  }

  getGameInfo() {
    console.log("getting info", this.state.totalWatts)
    return {
      totalWatts: this.state.totalWatts,
      dps: this.state.dps,
      miningPower: this.state.miningPower,
      shopData: this.state.shopData
    };
  }

  setGameInfo({totalWatts,dps,miningPower,shopData}) {
    console.log(totalWatts,dps,miningPower,shopData)
    this.setState({
      totalWatts: totalWatts,
      dps: dps,
      miningPower: miningPower,
      shopData: shopData
    });
  }
}

export default App;
