import HelperShop from "./HelperShop"

import React, {useState, useEffect} from "react"
import UpgradeShop from "./UpgradeShop"
function Shop({incrementDps, getTotalMoney, updateTotalMoney, getDps}) {
  const [dpsMult, setDpsMult] = useState(1)
  let mult = 1

  useEffect(() => {
    mult = dpsMult
  }, [dpsMult])

  function multDps(multiplier) {
    setDpsMult(d => d * multiplier)
    incrementDps((multiplier-1)*getDps())
    console.log(dpsMult)
  }
    return  ( 
    <div className="grid-div" id="right-div">

      <HelperShop
    incrementDps={incrementDps}
    getTotalMoney={getTotalMoney}
    updateTotalMoney={updateTotalMoney}
    dpsMult = {mult}
        />
       <UpgradeShop
       multDps={multDps}
       />

    </div>
    )


}

export default Shop
