import React from "react";
import Axios from "axios";

class Authentication extends React.Component {

  constructor({ getGameInfo, setGameInfo }) {
    this.getGameInfo = getGameInfo
    this.setGameInfo = setGameInfo
    this.state = {
      usernameReg: "",
      passwordReg: "",
      usernameLogin: "",
      passwordLogin: "",
    }
  }

   register = () => {
    Axios.post("http://localhost:3001/create-user", {
      username: this.state.usernameReg,
      password: this.state.passwordReg,
      gameInfo: this.state.getGameInfo,
    });
  };

   saveInfo = () => {
    console.log("save")
    Axios.post("http://localhost:3001/save-data", {
      username: this.state.usernameLogin,
      password: this.state.passwordLogin,
      gameInfo: this.state.getGameInfo,
    }).then(() => console.log("Auto Save"));
  }

   login = () => {
    Axios.post("http://localhost:3001/login", {
      username: this.state.usernameLogin,
      password: this.state.passwordLogin,
    }).then((response) => {
      console.log(response)
      this.setGameInfo(response.data)}
  )};

  componentDidMount() {
          console.log("mounted")
          this.interval = setInterval(this.saveInfo, 3000);
       }
       shouldComponentUpdate(nextProps, nextState){
        return false 
      }
       componentWillUnmount() {
        console.log("unmount")
         clearInterval(this.interval);
       }


