    return(
    <div className="grid-div" id="left-div">
      <h1>Sign Up</h1>

      <input
        className="textbox"
        placeholder="Username.."
        onChange={(e) => {
          this.setUsernameReg(e.target.value);
        }}
      ></input>

      <input
        className="textbox"
        placeholder="Password.."
        onChange={(e) => {
          this.setPasswordReg(e.target.value);
        }}
      ></input>

      <button onClick={this.register}>Enter</button>
