import React from "react";

class Upgrades extends React.Component {
  constructor(props) {
    super(props);
    this.text = props.text;
    this.upgradesClick = props.upgradesClick;
    this.cost = props.cost
    this.dps = props.dps
    this.helperCount = 2
    this.description = "test description this upgrade button works very well like really well"
  }
  render() {
    return (


      <div id="helper-container">
        <h3 className="right-aligned">#{this.helperCount}</h3>
        <div>
          <h2 className="block upgrade-title">{this.text}</h2>
          <h3 className="block ">DPS: {this.dps}</h3>
          <div className="upgrade-img-dsc-container">
            <image alt="placeholder"></image>
            <h3>{this.description}</h3>
          </div>
        </div>
      <button
        className="btn"
        onClick={() => {
          this.upgradeClicked();
        }}
      >
      </button>
      </div>
    );
  }
  // upgradeClicked() {
  //   var canUpgrade = this.upgradesClick(this.state.cost);
  //   if (canUpgrade === true) {
  //     this.setState({
  //       cost: Math.floor(this.state.cost * 2),
  //     });
  //   } else if (canUpgrade === "Maxed") {
  //     this.setState({ cost: canUpgrade });
  //   }
  // }

  upgradeClicked() {
   let didPurchase = this.upgradesClick(this.cost,this.dps)
   if(didPurchase) this.cost = (this.cost*1.2).toFixed(0)
}
}

export default Upgrades;
