import Helper from "./Helper"
function HelperShop({incrementDps, getTotalMoney, updateTotalMoney, getDpsMult}) {

    return  ( 
        <>
    <h1>Helper Shop</h1>
    <div className="upgrade-buttons-container">
    <Helper
      title="Dog Miner"
      cost={5}
      dps={1}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Helper
      title="Super Dog"
      cost={100}
      dps={8}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Helper
      title="Mining Drill"
      cost={500}
      dps={50}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

<Helper
      title="Mining Drill"
      cost={500}
      dps={50}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />
    </div>
    </>
    )

    function purchaseHelper(cost, dps) {
        if(getTotalMoney()-cost<0) return false
         updateTotalMoney(cost*-1)
         incrementDps(dps*getDpsMult())
         return true
     }

}

export default HelperShop
