import Upgrades from "./Upgrades"
function HelperShop({incrementDps, getTotalMoney, updateTotalMoney}) {

    function purchaseHelper(cost, dps) {
       if(getTotalMoney()-cost<0) return
        updateTotalMoney(cost*-1)
        incrementDps(dps)
    }
    return  ( 
    <div className="gridDiv" id="rightDiv">
    <h1>Helper Shop</h1>
    <div className="upgrade-buttons-container">
    <Upgrades
      text="Dog Miner"
      cost={100}
      dps={1}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Upgrades
      text="Super Dog"
      cost={1000}
      dps={7.5}
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Upgrades
      text="Mining Drill"
      cost={25000}
      dps={50}
      task="upgradeMoneyCount"
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />
    </div>
  </div>
    )


}

export default HelperShop
