import Upgrade from "./Upgrade"
function UpgradeShop({multDps}) {

    return  ( 
      <>
    <h1>Upgrades</h1>
    <div className="upgrade-buttons-container">

    <Upgrade
      title="Upgrade Mining"
      upgradeMult={2}
      description = " your mining strength" 
      cost={3000}
      upgradeClick={(cost) => handleUpgradeClick(cost,upgradeMult)}
    />

    <Upgrade
      title="Luck"
      task="upgradeLuck"
      description = " your mining strength" 
      upgradeMult={2}
      cost={5000}
      upgradesClick={(cost) => this.handleUpgradeClick(cost,upgradeMult)}
    />

    </div>
    </>
    )

    function handleUpgradeClick(cost,upgradeMult) {
      multDps(upgradeMult)
    }
}

export default UpgradeShop
