import React, { useState , useRef, useEffect} from "react";
import Axios from "axios";

const Authentication = React.memo(({ getGameInfo, setGameInfo }) => {
  const timerID = useRef()
  const [usernameReg, setUsernameReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");

  const [usernameLogin, setUsernameLogin] = useState("");
  const [passwordLogin, setPasswordLogin] = useState("");

  const register = () => {
    Axios.post("http://localhost:3001/create-user", {
      username: usernameReg,
      password: passwordReg,
      gameInfo: getGameInfo,
    });
  };

  const saveInfo = () => {
    console.log("save")
    Axios.post("http://localhost:3001/save-data", {
      username: usernameLogin,
      password: passwordLogin,
      gameInfo: getGameInfo,
    }).then((response) => console.log("Auto Save"));
  }

  const login = () => {
    Axios.post("http://localhost:3001/login", {
      username: usernameLogin,
      password: passwordLogin,
    }).then((response) => {
      console.log(response)
      setGameInfo(response.data)}
  )};

  window.onbeforeunload = () => {
    return saveInfo();
  };


//   class AutoSaveHandler{
//     componentDidMount() {
//       console.log("mounted")
//       this.interval = setInterval(saveInfo, 3000);
//    }
//    shouldComponentUpdate(nextProps, nextState){
//     return false 
//   }
//    componentWillUnmount() {
//     console.log("unmount")
//      clearInterval(this.interval);
//    }
//    render() {
//     return (
//      <>
//     </>
//     )
//   }
// }
  //let saveHandlerInstance = new AutoSaveHandler()

  useEffect(() => {
    timerID.current = setInterval(saveInfo, 6000); // 60000 milliseconds = 1 minute

    // return a cleanup function to stop the timer when the component unmounts
    return () => clearInterval(timerID.current);
  });






  return (
    <div className="grid-div" id="left-div">
      <h1>Sign Up</h1>

      <input
        className="textbox"
        placeholder="Username.."
        onChange={(e) => {
          setUsernameReg(e.target.value);
        }}
      ></input>

      <input
        className="textbox"
        placeholder="Password.."
        onChange={(e) => {
          setPasswordReg(e.target.value);
        }}
      ></input>

      <button onClick={register}>Enter</button>
      <h1>Login</h1>

      <input
        className="textbox"
        placeholder="Username.."
        onChange={(e) => {
          setUsernameLogin(e.target.value);
        }}
      ></input>

      <input
        className="textbox"
        placeholder="Password.."
        onChange={(e) => {
          setPasswordLogin(e.target.value);
        }}
      ></input>
      <button onClick={login}>Enter</button>
    </div>
  );
})

export default Authentication;
