function Wallet({ resourceClicked,  }) {
  const [totalResources,setTotalResources] = useState(100);
  const [currentResources,setCurrentResources] = useState(totalResources);
  return (
    <>
      <img
        id="wallet"
        src={process.env.PUBLIC_URL + "/wallet/Wallet-0.png"}
        alt="broken img"
        onClick={() => {
          handleResourceClick()

        }}
      ></img>
      <h1>
        {(currentResources/totalResources*100).toFixed(1) + "%"}
      </h1>
    </>
  );
}

function handleResourceClick() {
  resourceClicked(1)
  setCurrentResources(currentResources-1)
}





export default Wallet;
