import Upgrades from "./Upgrades"
function UpgradeShop() {

    return  ( 
    <div className="gridDiv" id="rightDiv">

    <h1>Upgrades</h1>
    <div className="upgrade-buttons-container">

    <Upgrades
      text="Upgrade Mining"
      upgradeMultiplier={2}
      upgradeClick={(cost) => handleUpgradeClick(cost)}
    />

    <Upgrades
      text="Luck"
      task="upgradeLuck"
      upgradesClick={(cost) => this.handleLuckUpgrade(cost)}
    />


    </div>
  </div>
    )

    function handleUpgradeClick(cost) {
      return true
    }
}

export default UpgradeShop
