import React from "react";
import Wallet from "./Wallet";
import Shop from "./shop/Shop"
import Authentication from "./Authentication";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      totalMoney: 0,
      dps: 0,
      // hph is the players harvest amount per hit
      hph: 1
    };
  }
  render() {
    return (
      <div id="body">
         <Authentication
          getGameInfo={this.getGameInfo()}
          setGameInfo={(info) => this.setGameInfo(info)}
        />
        <div className="grid-div" id="center-div">
          <h1>Money: {this.state.totalMoney}</h1>
          <h1 id="dps">DPS: {this.state.dps}</h1>
          <Wallet
            updateMoney={(moneyGained) => this.updateMoney(moneyGained)}
            resourcesMined={0}
            hph={this.state.hph}
            dps={this.state.dps}
          />
        </div>
        <Shop
          incrementDps = {(increment) => this.incrementDps(increment)}
          getTotalMoney = {() => this.getTotalMoney()}
          updateTotalMoney = {(cost) => this.updateMoney(cost)}
          getDps = {() => this.state.dps}
          multHph= {(multFactor) => this.multHph(multFactor)}
        />

      </div>
    );
  }

  incrementDps(increment) {
    this.setState({
      dps: this.state.dps + increment
    });
  }

  updateMoney(moneyToUpdate) {
    this.setState({
      totalMoney: this.state.totalMoney + moneyToUpdate
    })
  }

  multHph(multFactor) {
    this.setState({
      hph: this.state.hph * multFactor
    })
  }
  getTotalMoney() {
    return this.state.totalMoney
  }
  handleWalletClick(clickStrength,resourcesLeft) {
    var moneyGained = clickStrength
      // Set a state "fullyMined" to true that gets passed to the wallet so when fullyMined is true the wallet resets its variables
      // and then in this block, call a function like "resetResource" to reset it and get prepared to implement chest drops when the 
      // resource is fully mined.
    this.setState({
      totalMoney: this.state.totalMoney + moneyGained 
    });
  }




  getGameInfo() {
    return {
      totalMoney: this.state.totalMoney,
      clickSpeed: this.state.clickSpeed,
      luck: this.state.luck,
      moneyCount: document.getElementsByClassName("bill").length,
    };
  }

  setGameInfo(info) {
    console.log(info)
    this.setState({
      totalMoney: info.totalMoney,
      clickSpeed: info.clickSpeed,
      luck: info.luck,
      moneyCount: info.moneyCount + 1,
    });
  }
}

export default App;
