import React, {useState} from "react";

function Helper({title,upgradesClick,dps,helperCount, cost}) {
const description = "test description"
// const titleToPrice = {"Dog Miner": 5, "Super Dog": 50, "Mining Drill":200}
// const [cost,setCost] = useState(titleToPrice[title])

    return (
      <div id="helper-container">
        <h3 className="right-aligned">#{helperCount}</h3>
        <div>
          <h2 className="block upgrade-title">{title}</h2>
          <h3 className="block no-margin">{dps} DPS</h3>
          <div className="upgrade-img-dsc-container">
            <img alt="placeholder"/>
            <h3 className="no-margin">{description}</h3>
          </div>
        </div>
      <button
        className="btn block"
        onClick={() => {
          upgradeClicked();
        }}
      >
        ${cost}
      </button>
      </div>
    );


  function upgradeClicked() {
   let didPurchase = upgradesClick(cost,dps)
   if(didPurchase) setCost(c => (c *1.3).toFixed(0))
}
}

export default Helper;



















