import Upgrades from "./Upgrades"
function HelperShop({incrementDps, getTotalMoney, updateMoney}) {

    //console.log(totalMoney.total)
    function purchaseHelper(cost, dps) {
        console.log(getTotalMoney(),cost)
       if(getTotalMoney()-cost<0) return
       console.log("teeeeesttttt")
        updateMoney(cost*-1)
        incrementDps(dps)
    }
    return  ( 
    <div className="gridDiv" id="rightDiv">
    <h1>Helper Shop</h1>
    <div className="upgrade-buttons-container">
    <Upgrades
      text="Dog Miner"
      upgradesClick={(cost,dps) => purchaseHelper(cost, dps)}
    />

    <Upgrades
      text="Super Dog"
      upgradesClick={(cost) => this.handleLuckUpgrade(cost)}
    />

    <Upgrades
      text="Mining Drill"
      task="upgradeMoneyCount"
      upgradesClick={(cost) => this.handleMoneyCountUpgrade(cost)}
    />
    </div>
  </div>
    )


}

export default HelperShop
