import HelperShop from "./HelperShop"

import React, {useState} from "react"
import UpgradeShop from "./UpgradeShop"
// import UpgradeShop from "./UpgradeShop"
function Shop({incrementDps, getTotalMoney, updateTotalMoney}) {
    const [shopType, setShopType] = useState("helpers")
    return  ( 

    <div className="grid-div" id="right-div">

      <div className="shop-menu-bar">
        <button className="shop-btn" onClick={"helpers"}>Helpers</button>
        <button className="shop-btn" onClick={"upgrades"}>Upgrades</button>
      </div>

      {shopType==="helpers" &&  <HelperShop
    incrementDps={incrementDps}
    getTotalMoney={getTotalMoney}
    updateTotalMoney={updateTotalMoney}
        />}
        {shopType==="upgrades" && <UpgradeShop/>}
    </div>  


    )

    function handleButtonClick(buttonName) {
      setShopType(buttonName)
    }
}

export default Shop
