class Wallet extends Component {

   state = {
    totalResources: (1 + this.props.resourcesMined) * 10,
    currentResources: (1 + this.props.resourcesMined) * 10,
    canHarvest: true,
    shouldReset: false,
  };

  componentDidMount() {
    this.interval = setInterval(() => {
      this.harvestResource();
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  harvestResource = (clickStrength) => {
    console.log(this.props.dps);
    if (!this.state.canHarvest) return;
    let harvestAmount = clickStrength ? clickStrength : this.props.dps;
    //console.log(harvestAmount)
    this.props.updateMoney(harvestAmount);
    this.setState((state) => ({
      currentResources: state.currentResources - harvestAmount,
    }));
    if (this.state.currentResources <= 1) {
      this.handleResourceDepleted();
    }
  };

