import React, {useState} from "react";

function Wallet({ resourceClicked }) {
  const [totalResources,setTotalResources] = useState(1000);
  const [currentResources,setCurrentResources] = useState(totalResources);
  return (
    <>
      <img
        id="wallet"
        src={process.env.PUBLIC_URL + "/wallet/Wallet-0.png"}
        alt="broken img"
        onClick={() => {
          resourceClicked(1)
          setCurrentResources(currentResources-1)
        }}
      ></img>
      <h1>
        {(currentResources/totalResources*100).toFixed(2) + "%"}
      </h1>
    </>
  );
}

export default Wallet;
