import React from "react";

class Upgrade extends React.Component {
  constructor(props) {
    super(props);
    this.title = props.title;
    this.upgradeClick = props.upgradeClick;
    this.cost = props.cost
    this.upgradeMult = props.upgradeMult
    this.description = props.description
  }
  render() {
    return (
      <div id="upgrade-container">
        <div className="flex-jstfy-cent">
        <img alt="placeholder" id="upgrade-img"/>
        <div className="center-items">
          <h2 className="block upgrade-title">{this.title}</h2>
            <h3 className="no-margin">{this.upgradeMult +"x" + this.description}</h3>
          </div>
      <button
        className="btn inline small-btn"
        onClick={() => {
          this.upgradeClicked();
        }}
      >
        ${this.cost}
      </button>
        </div>

      </div>
    );
  }

  upgradeClicked() {
  let didPurchase = this.upgradeClick(this.cost,this.upgradeMult, this.title)
   if(didPurchase) this.cost = (this.cost*1.2).toFixed(0)
}
}

export default Upgrade;
