import Upgrades from "./Upgrades"
import React from "react"
class HelperShop extends React.Component() {

    constructor(props) {
        super(props)
        this.incrementDps = props.incrementDps
        this.totalMoney = props.totalMoney
        this.updateMoney = props.updateMoney
    }

    purchaseHelper(cost, dps) {
        console.log(this.totalMoney,cost)
        if(this.totalMoney-cost<0) return

        this.updateMoney(cost*-1)
        this.incrementDps(dps)
    }

    render()  { 
        return (
    <div className="gridDiv" id="rightDiv">
    <h1>Helper Shop</h1>
    <div className="upgrade-buttons-container">
    <Upgrades
      text="Dog Miner"
      task="upgradeClickSpeed"
      upgradesClick={(cost,dps) => this.purchaseHelper(cost, dps)}
    />

    <Upgrades
      text="Super Dog"
      task="upgradeLuck"
      upgradesClick={(cost) => this.handleLuckUpgrade(cost)}
    />

    <Upgrades
      text="Mining Drill"
      task="upgradeMoneyCount"
      upgradesClick={(cost) => this.handleMoneyCountUpgrade(cost)}
    />
    </div>
  </div>
    )}
}

export default HelperShop
