import Helper from "./Helper"
function HelperShop({incrementDps, getTotalMoney, updateTotalMoney, dpsMult,shopData, updateShopData}) {

  function renderHelper({title,cost,dps}) {
    return <Helper
      key={title}
      title={title}
      dps={dps * dpsMult}
      cost={cost}
      upgradesClick={(title,cost,dps) => purchaseHelper(title, cost, dps)}
    />
  }

    return  ( 
        <>
        <h1>Helper Shop</h1>
        <div className="upgrade-buttons-container">
        { shopData.map(data => {
          //If the data has a dps property it is for a helper component so it will be rendered
          // otherwise it is for an upgrade component and wont render
          return data?.dps ? renderHelper(data) : undefined
        }) }
        </div>
        </>
        )    

    function purchaseHelper(title, cost, dps) {
      console.log(shopData.numActive)
        if(getTotalMoney()<cost) return false

         updateShopData(title,{cost: (cost*1.5).toFixed(0), numActive: shopData.numActive+1})
         updateTotalMoney(cost*-1)
         incrementDps(dps)
         return true
     }
}

export default HelperShop
